def recite(start_verse, end_verse):
    d={1: 'first', 2: 'second', 3: 'third', 4: 'fourth', 5: 'fifth', 6: 'sixth', 7: 'seventh', 8: 'eighth', 9: 'ninth', 10: 'tenth', 11: 'eleventh', 12: 'twelfth'}
    l=['a Partridge in a Pear Tree.', 'two Turtle Doves', 'three French Hens', 'four Calling Birds', 'five Gold Rings', 'six Geese-a-Laying', 'seven Swans-a-Swimming', 'eight Maids-a-Milking', 'nine Ladies Dancing', 'ten Lords-a-Leaping', 'eleven Pipers Piping', 'twelve Drummers Drumming']
    return [ f"On the {d[len(l[:i+1])]} day of Christmas my true love gave to me: "+", ".join(l[:i+1][::-1][:-1])+', and '+l[:i+1][::-1][-1] if len(l[:i+1])>1 else f"On the {d[len(l[:i+1])]} day of Christmas my true love gave to me: "+l[:i+1][::-1][-1] for i in range(start_verse-1,end_verse)]